import React, { useState, useEffect } from "react";
import axios from "axios";

const Profile = () => {
    const [user, setUser] = useState(null);
    const token = localStorage.getItem("authToken");
    const userEmail = localStorage.getItem("userEmail"); // Get user email

    useEffect(() => {
        const fetchUserProfile = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/profile/${userEmail}`, {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });

                if (response.data.success) {
                    setUser(response.data.user);
                } else {
                    console.error("Error fetching user profile:", response.data.message);
                }
            } catch (error) {
                console.error("Error fetching user profile:", error);
            }
        };

        fetchUserProfile();
    }, []);

    if (!user) return <p>Loading profile...</p>;

    return (
        <div className="container mt-5">
            <h2>User Profile</h2>
            <img src={`http://localhost:5000${user.imageUrl}`} alt="Profile" style={{ width: "100px", borderRadius: "50%" }} />
            <p><strong>Email:</strong> {user.email}</p>
            <p><strong>Gender:</strong> {user.gender}</p>
            <p><strong>Phone Number:</strong> {user.number}</p>
            <p><strong>Age:</strong> {user.age}</p>
        </div>
    );
};

export default Profile;
