import React from "react";

const Cart = ({ cart, updateQuantity, removeFromCart }) => {
  const totalItems = cart.reduce((sum, item) => sum + item.quantity, 0);
  const totalPrice = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

  return (
    <div className="container mt-4">
      <h2 className="mb-3">üõí Shopping Cart</h2>

      {cart.length === 0 ? (
        <div className="alert alert-warning text-center">No items in the cart</div>
      ) : (
        <div className="card shadow-sm">
          <div className="card-body">
            <ul className="list-group">
              {cart.map((item) => (
                <li key={item.id} className="list-group-item d-flex justify-content-between align-items-center">
                  <div>
                    <strong>{item.name}</strong> <br />
                    Quantity: {item.quantity} | Price: ‚Çπ{item.price}
                  </div>
                  <div>
                    <button className="btn btn-sm btn-success mx-1" onClick={() => updateQuantity(item.id, 1)}>+</button>
                    <button className="btn btn-sm btn-warning mx-1" onClick={() => updateQuantity(item.id, -1)}>-</button>
                    <button className="btn btn-sm btn-danger mx-1" onClick={() => removeFromCart(item.id)}>‚ùå</button>
                  </div>
                </li>
              ))}
            </ul>

            <div className="mt-3 text-end">
              <h5>Total Items: <span className="badge bg-primary">{totalItems}</span></h5>
              <h5>Total Price: <span className="badge bg-success">‚Çπ{totalPrice}</span></h5>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Cart;
