import React from 'react'
import { useState } from 'react'
import axios from 'axios';
const Report = () => {
    const [report, setReport] = useState({ "conemail": "", "conname": "", "connumber": "", "conmess": "" });
    const onChanges = (e) => {
        setReport((user) => {
            return {
                ...user, [e.target.name]: e.target.value
            }
        })
    }
    const onSubmit__ = async(e) => {
            e.preventDefault();

            try{
                
                if(report.conemail && report.conname && report.connumber && report.conmess ){
                    const res = await axios.post('http://localhost:5000/report',report)
                    console.log(res);
                    setReport(()=>{
                        return {"conemail": "", "conname": "", "connumber": "", "conmess": ""}
                    }
                )
                }
                else{
                    console.log("fill form");
                }
            }
            catch(err){
                console.error("error spoted in report page" , err);
            }
            
    }


    return (
        <>
            <div >
                <form action='' encType='multipart/html'onSubmit={onSubmit__} >
                    <label>Email:</label>
                    <input type='email' name='conemail' value={report.conemail} onChange={onChanges}></input>
                    <label>Name:</label>
                    <input type='text' name='conname' value={report.conname} onChange={onChanges}></input>
                    <label>Mobile Number:</label>
                    <input type='number' name='connumber' value={report.connumber} onChange={onChanges}></input>
                    <label>Message:</label> 
                    <textarea rows='5' cols='3' name='conmess' value={report.conmess} onChange={onChanges}></textarea>
                    <button type='button'>Submit</button>
                </form>
            </div>
        </>
    )
}

export default Report
