import React from "react";

const Cart = ({ cart, updateQuantity, removeFromCart }) => {
    const totalItems = cart.reduce((sum, item) => sum + item.quantity, 0);
    const totalPrice = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

    return (
        <>
            <div className="container mt-4">
                <h2 className="mb-3">Shopping Cart</h2>
                {cart.length === 0 ? (
                    <div className="alert alert-warning text-center">No items in the cart</div>
                ) : (
                    <div className="cardd shadow-sm">
                        <div className="card-body">
                            <ul className="list-group">
                                {cart.map((item) => (
                                    <li key={item.id}>
                                        <span>
                                            {item.name} - Quantity: {item.quantity}
                                        </span>
                                        <div>
                                            <button onClick={() => updateQuantity(item.id, 1)}>+</button>
                                            <button onClick={() => updateQuantity(item.id, -1)}>-</button>
                                            <button onClick={() => removeFromCart(item.id)}>Remove</button>
                                        </div>
                                    </li>
                                ))}
                            </ul>
                            <p>Total Items: <span>{totalItems}</span></p>
                            <p>Total Price: <span>â‚¹{totalPrice}</span></p>
                        </div>
                        </div>
                )}
                    </div>
        </>
            );
};

            export default Cart;
