import React from 'react'
import { useState } from 'react';

const Cart = () => {
    const [cart, setCart] = useState([]);
    const addToCart = (item) => {
        const existingItem = cart.find((cartItem) => cartItem.id === item.id);

        if (existingItem) {
            const updatedCart = cart.map((cartItem) =>
                cartItem.id === item.id ? { ...cartItem, quantity: cartItem.quantity + 1 } : cartItem
            );
            setCart(updatedCart);
        } else {
            setCart([...cart, { ...item, quantity: 1 }]); // Fixed typo `quantiy` → `quantity`
        }
    };

    const removeFromCart = (id) => {
        setCart(cart.filter((item) => item.id !== id));
    };

    const updateQuantity = (id, amount) => {
        const updatedCart = cart
            .map((item) =>
                item.id === id ? { ...item, quantity: item.quantity + amount } : item
            )
            .filter((item) => item.quantity > 0); // This ensures no items with 0 quantity exist in the cart

        setCart(updatedCart);
    };
    const totalItems = cart.reduce((sum,item)=>
        sum+item.quantity, 0
    )
    const totalPrice = cart.reduce((sum,item)=> 
        sum+item.price *item.quantity ,0
    )
  return (
    <>
        <h1>Cart</h1>
                {cart.length === 0 && <p>No items in the cart</p>}
                {cart.length > 0 && (
                    <div>
                        <ul>
                            {cart.map((item) => (
                                <li key={item.id}>
                                    <span>{item.name} - Quantity: {item.quantity}</span>
                                    <div>
                                        <button onClick={() => updateQuantity(item.id, 1)}>+</button>
                                        <button onClick={() => updateQuantity(item.id, -1)}>-</button>
                                        <button onClick={() => removeFromCart(item.id)}>Remove</button>
                                    </div>
                                </li>
                            ))}
                        </ul>
                        <p>Total Items:<span>{totalItems}</span></p>
                        <p>Total Price:<span>{totalPrice}</span></p>
                    </div>
                )}
    </>
  )
}

export default Cart
