import React from "react";

const Cart = ({ cart, updateQuantity, removeFromCart }) => {
  const totalItems = cart.reduce((sum, item) => sum + item.quantity, 0);
  const totalPrice = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);
  const handleBuyNow = async (item) => {
    try {
        const response = await axios.post("http://localhost:5000/cart", {
            id: item.id,
            name: item.name,
            price: item.price,
            quantity: item.quantity,
            image: item.image
        });

        if (response.status === 201) {
            alert("Item purchased successfully and stored in database!");
        }
    } catch (error) {
        console.error("Error purchasing item", error);
        alert("Failed to purchase item");
    }
};

  return (
    <>
      <div className="container mt-4">
        <h2 className="mb-3">Shopping Cart</h2>

        {cart.length === 0 ? (
          <div className="alert alert-warning text-center">No items in the cart</div>
        ) : (
          <div className="card shadow-sm">
            <div className="card-body">
              <ul className="list-group">
                {cart.map((item) => (
                  <li key={item.id} className="list-group-item d-flex justify-content-between align-items-center">
                    <div className="d-flex align-items-center">
                      <img src={item.image} alt={item.name} className="img-fluid me-3" style={{ width: "80px", height: "80px", objectFit: "cover", borderRadius: "5px" }} />
                      <div>
                        <strong>{item.name}</strong> <br />
                        Quantity: {item.quantity} | Price: ₹{item.price}
                      </div>
                    </div>
                    <div>
                      <button className="btn btn-sm btn-success mx-1" onClick={() => updateQuantity(item.id, 1)}>+</button>
                      <button className="btn btn-sm btn-warning mx-1" onClick={() => updateQuantity(item.id, -1)}>-</button>
                      <button className="btn btn-sm btn-danger mx-1" onClick={() => removeFromCart(item.id)}>Remove</button>
                      <button className="btn btn-sm btn-success mx-1" onClick={() => handleBuyNow(item)}>Buy Now</button>
                    </div>
                    {/* <div>
                      <button className="btn btn-sm btn-success mx-1">BUy Now</button>
                    </div> */}
                  </li>
                ))}
              </ul>

              <div className="mt-3 text-end">
                <h5>Total Quantity: <span>{totalItems}</span></h5>
                <h5>Total Price: <span>₹{totalPrice}</span></h5>
              </div>
            </div>
          </div>
        )}
      </div>
      
    </>
  );
};

export default Cart;
