import React, { useState } from "react";
import axios from "axios";

const RegisterForm = () => {
  const [register, setRegister] = useState({
    email: "",
    password: "",
    gender: "",
    number: "",
    age: "",
  });

  const on_Changes = (e) => {
    setRegister((prevRegister) => ({
      ...prevRegister,
      [e.target.name]: e.target.value,
    }));
  };

  const on_submits = async (e) => {
    e.preventDefault();
    try {
      if (register.email && register.password && register.gender && register.number && register.age) {
        const registerData = await axios.post("http://localhost:5000/register", register);
        console.log(registerData);

        setRegister({
          email: "",
          password: "",
          gender: "",
          number: "",
          age: "",
        });
      }
    } catch (error) {
      console.error("Error occurred while registering:", error);
    }
  };

  return (
    <div className="container mb-2">
      <div className="row d-flex justify-content-center align-items-center">
        <div className="col-12 col-lg-6 p-3">
          <h2>Register Form</h2>
          <form onSubmit={on_submits}>
            <div className="form-group mt-2">
              <label>Email:</label>
              <input
                type="email"
                name="email"
                className="form-control"
                placeholder="Enter your email"
                value={register.email}
                onChange={on_Changes}
                required
              />
            </div>

            <div className="form-group mt-2">
              <label>Password:</label>
              <input
                type="password"
                name="password"
                className="form-control"
                placeholder="Enter your password"
                value={register.password}
                onChange={on_Changes}
                required
              />
            </div>

            <div className="form-group mt-2">
              <label>Gender:</label>
              <div>
                <input
                  type="radio"
                  name="gender"
                  value="Male"
                  checked={register.gender === "Male"}
                  onChange={on_Changes}
                  required
                />{" "}
                Male
                <input
                  type="radio"
                  name="gender"
                  value="Female"
                  checked={register.gender === "Female"}
                  onChange={on_Changes}
                  required
                />{" "}
                Female
                <input
                  type="radio"
                  name="gender"
                  value="others"
                  checked={register.gender === "others"}
                  onChange={on_Changes}
                  required
                />{" "}
                Female
              </div>
            </div>

            <div className="form-group mt-2">
              <label>Phone Number:</label>
              <input
                type="number"
                name="number"
                className="form-control"
                placeholder="Enter your number"
                value={register.number}
                onChange={on_Changes}
                required
              />
            </div>

            <div className="form-group mt-2">
              <label>Age:</label>
              <input
                type="number"
                name="age"
                className="form-control"
                placeholder="Enter your age"
                value={register.age}
                onChange={on_Changes}
                required
              />
            </div>

            <button type="submit" className="btn btn-primary px-3 mt-3">
              Submit
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default RegisterForm;
