import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import { useState } from 'react';
import Navbar from './Components/Navbar/Navbar';
import {BrowserRouter,Routes,Route} from 'react-router-dom';
import Footer from './Components/Footer/Footer';
import Caro from './Components/Carousel/Caro'
import Men from './Pages/Men/Men';
import Contact from './Pages/Contact/Contact';
import Cart from './Pages/Cart/Cart';
import MenTshirt from './Pages/MenTshirt/MenTshirt';
function App() {
  const [cart, setCart] = useState([]);

  const addToCart = (item) => {
    const existingItem = cart.find((cartItem) => cartItem.id === item.id);

    if (existingItem) {
      setCart(
        cart.map((cartItem) =>
          cartItem.id === item.id
            ? { ...cartItem, quantity: cartItem.quantity + 1 }
            : cartItem
        )
      );
    } else {
      setCart([...cart, { ...item, quantity: 1 }]);
    }
  };

  const removeFromCart = (id) => {
    setCart(cart.filter((item) => item.id !== id));
  };

  const updateQuantity = (id, amount) => {
    setCart(
      cart
        .map((item) =>
          item.id === id ? { ...item, quantity: item.quantity + amount } : item
        )
        .filter((item) => item.quantity > 0)
    );
  };
  return (
    <>
      <BrowserRouter>
        <Navbar/>

        <Routes>
        <Route path='/' element={<Caro />}></Route>
        <Route path='/cart' element={<Cart cart={cart} updateQuantity={updateQuantity} removeFromCart={removeFromCart}/>}></Route>
        <Route path='/contact' element={<Contact/>}></Route>
        <Route path='/tshirt' element={<MenTshirt addToCart={addToCart}/>}></Route>
        <Route path='/men' element={<Men addToCart={addToCart}/>}></Route>
        </Routes>
        <Footer/>
      </BrowserRouter>
    </>
  );
}

export default App;
